#include <fstream>
#include <filesystem>

#include "gtest/gtest.h"
#include "../chip8cpp/Chip8Core.h"

namespace
{
    std::optional<std::filesystem::path> find_roms_path()
    {
        const std::string searchDir = "roms";
        auto current_path = std::filesystem::current_path();
        while (current_path != current_path.root_path())
        {
            auto search_path = current_path / searchDir;
            if (exists(search_path))
            {
                return search_path;
            }
            current_path = current_path.parent_path();
        }

        return std::nullopt;
    }

    std::optional<std::filesystem::path> find_rom(const std::string& name)
    {
        auto roms_path = find_roms_path();
        if (!roms_path)
        {
            return std::nullopt;
        }
        return roms_path.value() / name;
    }

    static std::optional<std::vector<uint8_t>> read_test_rom(const std::string& name)
    {
        auto rom_path = find_rom(name);
        if (!rom_path)
        {
            return std::nullopt;
        }

        // Open the file in binary mode and move the cursor to the end
        std::ifstream file(rom_path->string(), std::ios::binary | std::ios::ate);
        if (!file)
        {
            return std::nullopt;
        }

        auto filesize = file.tellg();
        std::vector<uint8_t> buffer(filesize);
        file.seekg(0, std::ios::beg);
        if (!file.read(reinterpret_cast<char*>(buffer.data()), filesize))
        {
            return std::nullopt;
        }

        return buffer;
    }
}

